Dashmix.onLoad((() => class {
    static initValidation() {
        Dashmix.helpers("jq-validation"), jQuery(".js-validation-signin").validate({
            rules: {
                "login-id": {
                    required: !0,
                },
                "login-password": {
                    required: !0,
                }
            },
            messages: {
                "login-id": {
                    required: "Lütfen ID veya Telefon Numarası girin",
                },
                "login-password": {
                    required: "Lütfen parolanızı girin",
                }
            }
        })
    }
    static init() { this.initValidation() }
}.init()));